import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { RandomWheelName } from '../../../utils';

const colors = ['#00E8FC', '#F96E46', '#F9C846', '#FFE3E3'];

const WheelName = (props) => {
  console.log('RandomWheelName', RandomWheelName);

  const [names, setName] = useState('');
  const [options, setOptions] = useState({
    el: '#wheel', // Canvas ID
    members: [], // Array of members
    colors: [], // Background color of each member
    radius: 250, // wheel radius
  });

  useEffect(() => {
    var wheel = new RandomWheelName(options);
    wheel.init();
  }, [options]);

  const convertValue = (value) => {
    if (!value) {
      return;
    }

    const arr = value.split('/n');
  };

  const onChange = (e) => {
    const { value } = e.target;
    setName(value);
  };

  return (
    <>
      <canvas id='wheel'></canvas>
      <textarea name={'name'} value={names} onChange={onChange}></textarea>
    </>
  );
};

WheelName.propTypes = {};

export default WheelName;
