import React, { useState } from "react";
import PropTypes from "prop-types";

import "./RandomNumber.scss";

const randomNumber = (props) => {
  const [state, setState] = useState({ min: 0, max: 100 });
  const [result, setResult] = useState(null);

  const onChange = (e) => {
    let { name, value } = e.target;
    value = parseInt(value);
    console.log(value);
    setState((prev) => ({ ...prev, [name]: parseInt(value) }));
  };

  const getRandomInt = (max) => {
    return Math.floor(Math.random() * Math.floor(max));
  };

  const random = () => {
    const { max, min } = state;

    return setResult(getRandomInt(max - min) + min);
  };

  return (
    <div className="random--number">
      <h3>True Random Number Generator</h3>
      <div>
        Min:
        <input name="min" value={state.min} onChange={onChange} type="number" />
      </div>
      <div>
        Max:
        <input name="max" value={state.max} onChange={onChange} type="number" />
      </div>

      <div>
        <button onClick={random}>Generate</button>
      </div>

      <div>
        <p>Result</p>
        <p>{result !== null ? result : ""}</p>
      </div>
    </div>
  );
};

randomNumber.propTypes = {};

export default randomNumber;
