import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { RandomWheelName } from '../../../utils';

var settings = {
  el: '#wheel', // Canvas ID
  members: ['Hamburgers', 'Tacos', 'Steak', 'Burritos'], // Array of members
  colors: ['#C7181D', '#FCB937', '#A1B836', '#371979'], // Background color of each member
  radius: 250, // wheel radius
};

const WheelName = (props) => {
  console.log('RandomWheelName', RandomWheelName);

  const [names, setName] = useState('');

  useEffect(() => {
    var wheel = new RandomWheelName(settings);
    wheel.init();
  }, []);

  return (
    <>
      <canvas id='wheel'></canvas>
      <textarea name={'name'} value={names}></textarea>
    </>
  );
};

WheelName.propTypes = {};

export default WheelName;
