import React, { useState } from "react";
import PropTypes from "prop-types";

import "./RandomNumber.scss";

const randomNumber = (props) => {
  const [state, setState] = useState({ min: 0, max: 100 });

  const onChange = (e) => {
    const { name, value } = e.target;
    setState((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <div className="random--number">
      <h3>True Random Number Generator</h3>
      <div>
        Min:
        <input name="min" value={state.min} onChange={onChange} />
      </div>
      <div>
        Max:
        <input name="max" value={state.max} onChange={onChange} />
      </div>

      <div>
        <button>Generate</button>
      </div>

      <div>
        <p>Result</p>
        <p>KQ</p>
      </div>
    </div>
  );
};

randomNumber.propTypes = {};

export default randomNumber;
